INSERT INTO `email_templates` VALUES (1,'User Registration','imagefootage.dev@gmail.com','Admin','Registration','<p>Hello ##first_name##,</p>\n<p><br />Thank you for registering with us<br /><br />To activate your account please click on the Activation Link below:<br /><br />##activation_link##<br /><br /><br />Your Login Detail are as followed. <br /><br />User Name: ##email_address##<br />Password: ##password##</p>\n<p>Thank you</p><br>\n<p>Image Footage Team</p> ',NULL,NULL),(2,'Forget password','imagefootage.dev@gmail.com','Admin','Forget Password','<p>Dear ##user_name##,<br /><br />Your password has been reset. Below are the login details for your account.<br /><br />User Email: ##email_address##<br />Password: ##password##</p>\n<p><br /><br />Thank you,<br />Image Footage Team</p>',NULL,NULL),(3,'product Status','imagefootage.dev@gmail.com','Admin','Status','<p>Hello ##first_name##,</p> <p><br />Thank you for registering with us<br /><br />Your product <b>##productname##</b> has been <b>##status##</b> ##Rejection_region##<br /></p> <p>Thank you</p><br> <p>Image Footage Team</p> ',NULL,NULL),(7,'Order Confirmation','imagefootage.dev@gmail.com','Order Invoice','Order Invoice','<p>Hello ##first_name##,</p>\r\n<h2>Thanks for placing your order.</h2>\r\n<h4>Your order # is ##orderId##</h4>\r\n<p>&nbsp;</p>\r\n<h4>you have purchased:</h4>\r\n<p>##productname##</p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"##logo##\" alt=\"imagefootageTeam\" width=\"150\" height=\"auto\" /></p>\r\n<p>Thank you,</p>\r\n<p>Image Footage Team</p>',NULL,NULL),(8,'Admin order','imagefootage.dev@gmail.com','Order Invoice','Buyer Invoice','<p>Hi Admin image footage team,</p>\r\n<h2>##buyername## buy a product .</h2>\r\n<h4>where orderId # is ##orderId##</h4>\r\n<p>where ordered by  email is <strong>##orderemail##</strong></p>\r\n<p>order total amount is <strong>##ordertotal##</strong></p>\r\n<h4>they have purchased:</h4>\r\n<p>##productname##</p>\r\n<p><img src=\"##logo##\" alt=\"imagefootageTeam\" width=\"150\" height=\"auto\" /></p>\r\n<p>Thank you,</p>\r\n<p>Image Footage Team</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,NULL),(9,'Product upload status','imagefootage.dev@gmail.com','Imagefootages','Product upload successfully','<p>Dear ##firstname## <br /> We have  received your product ##productName## . we let you inform by e-mail about the status of your products shortly.</p>\r\n<p><img src=\"##logo##\" alt=\"imagefootageTeam\" width=\"150\" height=\"auto\" /></p>\r\n<p>Thank you,</p>\r\n<p>Image Footage Team</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>',NULL,NULL),(10,'Error meassage','imagefootage.dev@gmail.com','Admin','Error meassage','<p>Hello Admin,</p>\n<p><br />Error in following <br /><br />##error##</p> ',NULL,NULL),(11,'Main Invoice','imagefootage.dev@gmail.com','Order Invoice','Order Invoice','<p>Hello ##first_name##,</p>\r\n<h2>Thanks for placing your order.</h2>\r\n<h4>Your order # is ##orderId##</h4>\r\n<p>&nbsp;</p>\r\n<h4>you have purchased:</h4>\r\n<p>##productname##</p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"##logo##\" alt=\"imagefootageTeam\" width=\"150\" height=\"auto\" /></p>\r\n<p>Thank you,</p>\r\n<p>Image Footage Team</p>',NULL,'2'),(12,'Invoice 2','imagefootage.dev@gmail.com','Order Invoice','Order Invoice','<p>Hello ##first_name##,</p>\r\n<h2>Thanks for placing your order on Site 2.</h2>\r\n<h4>Your order # is ##orderId##</h4>\r\n<p>&nbsp;</p>\r\n<h4>you have purchased:</h4>\r\n<p>##productname##</p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"##logo##\" alt=\"imagefootageTeam\" width=\"150\" height=\"auto\" /></p>\r\n<p>Thank you,</p>\r\n<p>Image Footage Team 2</p>',NULL,'2');
